chapter.code:
stage('Qodana') {
    agent {
        docker {
            image 'jetbrains/qodana-<linter>'
            args '-v <project-directory>:/data/project/'
            args '--entrypoint=""'
        }
    }
    environment {
        QODANA_TOKEN = credentials('qodana-token')
    }
    steps {
        sh "qodana --save-report --send-report"
    }
}

job("Qodana") {
    container("jetbrains/qodana-jvm-community:2021.3") {
        // https://www.jetbrains.com/help/space/secrets-and-parameters.html
        env["QODANA_TOKEN"] = Secrets("qodana-token")
        shellScript {
            content = """
    qodana --send-report
""".trimIndent()
        }
    }
}
