chunk.code:
pipeline {
   environment {
      QODANA_TOKEN=credentials('qodana-token')
    }
   agent {
      docker {
         args '''
            -v <path-to-project>:/data/project
            --entrypoint=""
         '''
         image 'jetbrains/qodana-<linter>'
      }
   }
   stages {
      stage('Qodana') {
         steps {
            sh '''
               qodana \
               --fail-threshold <number>
            '''
         }
      }
   }
}
